import React, { Component } from "react";
import Input from "./sharedComponents/Input";
import TextArea from "./sharedComponents/TextArea";

// import moment from 'moment';

class OfferForm extends Component {
  showDeleteBtn() {
    if (this.props.deleteOffer) {
      return (
        <button
          onClick={(e) => {
            e.preventDefault();
            this.props.deleteOffer(this.props.offer._id);
          }}
          className="btn btn-danger"
          style={{ position: "absolute", right: "25%", width: "90px" }}
        >
          Изтрий
        </button>
      );
    }
    return null;
  }

  showEditBtn() {
    if (this.props.editOffer) {
      return (
        <button
          onClick={(e) => {
            e.preventDefault();
            this.props.editOffer(this.props.offer._id);
          }}
          className="btn btn-primary"
        >
          Промени
        </button>
      );
    }
    return null;
  }

  render() {
    return (
      <div>
        <form>
          <div className="row">
            <div className="col-md-12"></div>
            <div className="col-sm-4">
              <Input
                name="number"
                label="Номер на оферта"
                type="number"
                val={this.props.offer.number}
                changeFn={this.props.changeFn}
              />

              <Input
                name="area"
                label="Квадратура"
                type="number"
                val={this.props.offer.area}
                changeFn={this.props.changeFn}
              />

              <Input
                name="description"
                label="Описание"
                type="text"
                val={this.props.offer.description}
                changeFn={this.props.changeFn}
              />

              <Input
                name="floor"
                label="Етаж"
                type="number"
                val={this.props.offer.floor}
                changeFn={this.props.changeFn}
              />

              <Input
                name="phoneNumber"
                label="Телефон"
                type="number"
                val={this.props.offer.phoneNumber}
                changeFn={this.props.changeFn}
              />
            </div>

            <div className="col-sm-4">
              <Input
                name="price"
                label="Цена"
                type="number"
                val={this.props.offer.price}
                changeFn={this.props.changeFn}
              />

              <Input
                name="address"
                label="Адрес"
                type="text"
                val={this.props.offer.address}
                changeFn={this.props.changeFn}
              />

              <TextArea
                name="info"
                label="Допълнителна Информация"
                type="text"
                val={this.props.offer.info}
                changeFn={this.props.changeFn}
              />

              <Input
                name="owner"
                label="Име на Собственик"
                type="text"
                val={this.props.offer.owner}
                changeFn={this.props.changeFn}
              />

              <Input
                name="phoneNumber2"
                label="Телефон 2"
                type="number"
                val={this.props.offer.phoneNumber2}
                changeFn={this.props.changeFn}
              />
            </div>

            <div className="col-sm-4">
              <Input
                type="text"
                label="Вид Строителство"
                placeholder="Вид Строителсво"
                name="construction"
                val={this.props.offer.construction}
                changeFn={this.props.changeFn}
              />

              <Input
                type="text"
                label="Вид Имот"
                placeholder="Вид Имот"
                name="property"
                val={this.props.offer.property}
                changeFn={this.props.changeFn}
              />

              <Input
                type="text"
                label="Състояние"
                placeholder="Състояние"
                name="state"
                val={this.props.offer.state}
                changeFn={this.props.changeFn}
              />

              <Input
                type="text"
                label="Квартал"
                placeholder="Квартал"
                name="neighborhood"
                val={this.props.offer.neighborhood}
                changeFn={this.props.changeFn}
              />

              <Input
                name="phoneNumber3"
                label="Телефон 3"
                type="number"
                val={this.props.offer.phoneNumber3}
                changeFn={this.props.changeFn}
              />
            </div>
          </div>

          <div style={{ position: "relative" }}>
            {!this.props.editOffer && (
              <button
                onClick={(e) => {
                  e.preventDefault();
                  this.props.btn(this.props.offer);
                  // this.props.submitForm(this.state);
                }}
                className="btn btn-success"
                style={{ marginRight: "50px" }}
              >
                Запази
              </button>
            )}
            {this.showEditBtn()}
            {this.showDeleteBtn()}
          </div>
        </form>
      </div>
    );
  }
}

export default OfferForm;
